mapper.update:
update seer_msg
set msg_user_from_id = #{msgUserFromId,jdbcType=INTEGER},
  msg_user_accept_id = #{msgUserAcceptId,jdbcType=INTEGER},
  msg_time = #{msgTime,jdbcType=INTEGER},
  msg_content = #{msgContent,jdbcType=VARCHAR}
where msg_id = #{msgId,jdbcType=INTEGER}

mapper.select:
select msg_id, msg_user_from_id, msg_user_accept_id, msg_time, msg_content
from seer_msg
where msg_id = #{msgId,jdbcType=INTEGER}
