mapper.update:
update seer_admin_info
set admin_user_id = #{adminUserId,jdbcType=INTEGER},
  admin_type_id = #{adminTypeId,jdbcType=INTEGER},
  admin_time = #{adminTime,jdbcType=TIMESTAMP}
where admin_id = #{adminId,jdbcType=INTEGER}

mapper.select:
select admin_id, admin_user_id, admin_type_id, admin_time
from seer_admin_info
where admin_id = #{adminId,jdbcType=INTEGER}
